data class TabItem(
    val title: String,
    val unselectedIcon: androidx.compose.ui.graphics.vector.ImageVector,
    val selectedIcon: ImageVector,
)

// Source: https://www.youtube.com/watch?v=9r4st6dmyNE
@OptIn(androidx.compose.foundation.ExperimentalFoundationApi::class) // To be able to use: rememberPagerState
@androidx.compose.runtime.Composable
fun ScreenSwipeableTabRows(modifier: androidx.compose.ui.Modifier = Modifier) {

    val tabItems = listOf(
        TabItem(
            title = "Home",
            unselectedIcon = androidx.compose.material.icons.Icons.Outlined.Home,
            selectedIcon = Icons.Filled.Home,
        ),
        TabItem(
            title = "Browse",
            unselectedIcon = Icons.Outlined.ShoppingCart,
            selectedIcon = Icons.Filled.ShoppingCart,
        ),
        TabItem(
            title = "Account",
            unselectedIcon = Icons.Outlined.AccountCircle,
            selectedIcon = Icons.Filled.AccountCircle,
        ),
    )

    // https://mvnrepository.com/artifact/androidx.compose.material/material-icons-extended-android
    // implementation 'androidx.compose.material:material-icons-extended-android:1.5.4'

    var selectedTabIndex by androidx.compose.runtime.saveable.rememberSaveable { androidx.compose.runtime.mutableIntStateOf(0) }
    // Error: Type 'PagerState' has no method 'getValue(Nothing?, KProperty<*>)' and thus it cannot serve as a delegate
    // Solution: do not use by, but simply the =
    val pagerState = androidx.compose.foundation.pager.rememberPagerState(pageCount = {
        tabItems.size
    })

    androidx.compose.runtime.LaunchedEffect(key1 = selectedTabIndex, block = {pagerState.animateScrollToPage(selectedTabIndex)})
    LaunchedEffect(key1 = pagerState.currentPage, key2 = pagerState.isScrollInProgress, block = {
        if( !pagerState.isScrollInProgress){
            selectedTabIndex = pagerState.currentPage
        }
    })

    androidx.compose.foundation.layout.Column(
        modifier = modifier.fillMaxSize(1f)
    ) {
        // If more than 3 items int the list, then use ScrollableTabRow instead of TabRow
        // ScrollableTabRow(selectedTabIndex = selectedTabIndex) {
        androidx.compose.material3.TabRow(selectedTabIndex = selectedTabIndex) {
            tabItems.forEachIndexed { index, item: TabItem ->
                androidx.compose.material3.Tab(selected = index == selectedTabIndex, onClick = { selectedTabIndex = index }, text = { androidx.compose.material3.Text(text = item.title) }, icon = {
                    androidx.compose.material3.Icon(
                        imageVector = if (index == selectedTabIndex) {
                            item.selectedIcon
                        } else {
                            item.unselectedIcon
                        }, contentDescription = item.title
                    )
                })
            }
        }
        androidx.compose.foundation.pager.HorizontalPager(
            state = pagerState, modifier = Modifier
                .fillMaxSize(1f)
                .weight(1f)
        ) { index ->
            androidx.compose.foundation.layout.Box(modifier = Modifier.fillMaxSize(1f), contentAlignment = androidx.compose.ui.Alignment.Center) {
                Text(text = tabItems[index].title)
            }
        }
    }
}

