@androidx.compose.runtime.Composable
fun AlertDialogExample(
	onDismissRequest: () -> Unit,
	onConfirmation: () -> Unit,
	dialogTitle: String,
	dialogText: String,
	icon: androidx.compose.ui.graphics.vector.ImageVector,
) {
	androidx.compose.material3.AlertDialog(
		icon = {
			androidx.compose.material3.Icon(icon, contentDescription = "Example Icon")
		},
		title = {
			androidx.compose.material3.Text(text = dialogTitle)
		},
		text = {
			Text(text = dialogText)
		},
		onDismissRequest = {
			onDismissRequest()
		},
		confirmButton = {
			androidx.compose.material3.TextButton(
				onClick = {
					onConfirmation()
				}
			) {
				Text("Confirm")
			}
		},
		dismissButton = {
			TextButton(
				onClick = {
					onDismissRequest()
				}
			) {
				Text("Dismiss")
			}
		}
	)
}

@Composable
fun DialogExamples() {
    var openAlertDialog by androidx.compose.runtime.remember { androidx.compose.runtime.mutableStateOf(false) }

    androidx.compose.foundation.layout.Column {
        androidx.compose.material3.Button(
            modifier = androidx.compose.ui.Modifier.fillMaxWidth(1f),
            onClick = { openAlertDialog = true }) {
            Text(text = "Open Alert Dialog")
        }
    }

    when {
        // ...
        openAlertDialog -> {
            AlertDialogExample(
                onDismissRequest = { openAlertDialog = false },
                onConfirmation = {
                    openAlertDialog = false
                    println("Confirmation registered") // Add logic here to handle confirmation.
                },
                dialogTitle = "Alert dialog example",
                dialogText = "This is an example of an alert dialog with buttons.",
                icon = androidx.compose.material.icons.Icons.Default.Info
            )
        }
    }
}

