@OptIn(androidx.compose.material3.ExperimentalMaterial3Api::class)
@androidx.compose.runtime.Composable
fun SettingsClickableComp(
    @androidx.annotation.DrawableRes icon: Int,
    @androidx.annotation.StringRes iconDesc: Int,
    @StringRes name: Int,
    onClick: () -> Unit
) {
    androidx.compose.material3.Surface(
        color = androidx.compose.ui.graphics.Color.Transparent,
        modifier = androidx.compose.ui.Modifier
            .fillMaxWidth()
            .padding(16.dp),
        onClick = onClick,
    ) {
        androidx.compose.foundation.layout.Column {
            androidx.compose.foundation.layout.Row(
                verticalAlignment = androidx.compose.ui.Alignment.CenterVertically,
                horizontalArrangement = androidx.compose.foundation.layout.Arrangement.SpaceBetween
            ) {
                Row(verticalAlignment = Alignment.CenterVertically) {
                    androidx.compose.material3.Icon(
                        androidx.compose.ui.res.painterResource(id = icon),
                        contentDescription = androidx.compose.ui.res.stringResource(id = iconDesc),
                        modifier = Modifier
                            .size(24.dp)
                    )
                    androidx.compose.foundation.layout.Spacer(modifier = Modifier.width(8.dp))
                    androidx.compose.material3.Text(
                        text = stringResource(id = name),
                        style = androidx.compose.material3.MaterialTheme.typography.bodyMedium.copy(
                            color = MaterialTheme.colorScheme.surfaceTint
                        ),
                        modifier = Modifier
                            .padding(16.dp),
                        textAlign = androidx.compose.ui.text.style.TextAlign.Start,
                        overflow = androidx.compose.ui.text.style.TextOverflow.Ellipsis,
                    )
                }
                Spacer(modifier = Modifier.weight(1.0f))
                Icon(
                    androidx.compose.material.icons.Icons.Rounded.KeyboardArrowRight,
                    tint = MaterialTheme.colorScheme.surfaceTint,
                    contentDescription = stringResource(id = R.string.ic_arrow_forward)
                )
            }
            androidx.compose.material3.Divider()
        }

    }
}

