@Composable
fun FourColumnColors(
	firstText: String,
	secondText: String,
	thirdText: String,
	fourthText: String,
	firstColor: androidx.compose.ui.graphics.Color,
	secondColor: Color,
	thirdColor: Color,
	fourthColor: Color,
	modifier: Modifier
) {
	Column(modifier = modifier.padding(4.dp)) {
		androidx.compose.foundation.layout.Box(
			modifier = Modifier
				.background(firstColor)
				.fillMaxWidth(1f)
				.padding(vertical = 16.dp)
		) {
			Text(text = firstText, color = secondColor)
		}
		Box(
			modifier = Modifier
				.background(secondColor)
				.fillMaxWidth(1f)
				.padding(vertical = 16.dp)
		) {
			Text(text = secondText, color = firstColor)
		}
		Box(
			modifier = Modifier
				.background(thirdColor)
				.fillMaxWidth(1f)
				.padding(vertical = 16.dp)
		) {
			Text(text = thirdText, color = fourthColor)
		}
		Box(
			modifier = Modifier
				.background(fourthColor)
				.fillMaxWidth(1f)
				.padding(vertical = 16.dp)
		) {
			Text(text = fourthText, color = thirdColor)
		}
	}
}

