//Source: https://betterprogramming.pub/how-to-get-activity-from-jetpack-compose-d0af406d534f
// Usage example, in compose:
//val context = LocalContext.current
//val activity = context.findActivity()
fun android.content.Context.findActivity(): android.app.Activity {
    var context = this
    while (context is android.content.ContextWrapper) {
        if (context is Activity) return context
        context = context.baseContext
    }
    throw IllegalStateException("no activity")
}

val rawHTML = stringResource(id = R.string.about)

// Display HTML in Compose View: Not supported Yet => Use Android View
// Source: https://medium.com/@theAndroidDeveloper/displaying-html-text-in-jetpack-compose-7b801bb028c6
AndroidView(modifier = modifier.padding(dimensions.spaceMedium),
    factory = { MaterialTextView(it) },
    update = { it.text = HtmlCompat.fromHtml(rawHTML, HtmlCompat.FROM_HTML_MODE_COMPACT) })
