private android.graphics.Bitmap cropSquare(Bitmap srcBmp) {
    Bitmap dstBmp;

    if (srcBmp.getWidth() >= srcBmp.getHeight()){

        dstBmp = Bitmap.createBitmap(
                srcBmp,
                srcBmp.getWidth()/2 - srcBmp.getHeight()/2,
                0,
                srcBmp.getHeight(),
                srcBmp.getHeight()
        );

    }else{

        dstBmp = Bitmap.createBitmap(
                srcBmp,
                0,
                srcBmp.getHeight()/2 - srcBmp.getWidth()/2,
                srcBmp.getWidth(),
                srcBmp.getWidth()
        );
    }
    return dstBmp;
}

//The new size we want to scale to
// TODO : move to the field class 
final int REQUIRED_SIZE=70;

/**
* Method to decode image and scales it to reduce memory consumption
*/
private android.graphics.Bitmap decodeFile(java.io.File f){
    try {
        //Decode image size
        android.graphics.BitmapFactory.Options o = new BitmapFactory.Options();
        o.inJustDecodeBounds = true;
        BitmapFactory.decodeStream(new java.io.FileInputStream(f),null,o);

        //Find the correct scale value. It should be the power of 2.
        int scale=1;
        while(o.outWidth/scale/2>=REQUIRED_SIZE && o.outHeight/scale/2>=REQUIRED_SIZE)
            scale*=2;

        //Decode with inSampleSize
        BitmapFactory.Options o2 = new BitmapFactory.Options();
        o2.inSampleSize=scale;
        return BitmapFactory.decodeStream(new FileInputStream(f), null, o2);
    } catch (java.io.FileNotFoundException e) {}
    return null;
}

