public static boolean isIntentAvailable(android.content.Context ctx, android.content.Intent intent) {
    final android.content.pm.PackageManager mgr = ctx.getPackageManager();
    java.util.List<android.content.pm.ResolveInfo> list = mgr.queryIntentActivities(intent, PackageManager.MATCH_DEFAULT_ONLY);
    return list.size() > 0;
}

