private static final String TAG = "$className$";
private static final String SHARED_PREFS_UUID = "$className$";

private static final $className$ ourInstance = new $className$();
private String uuid;

public static $className$ getInstance() {
    return ourInstance;
}

    private $className$() {}

public String getUuid(android.content.Context ctx) {
    if(uuid == null || uuid.isEmpty()){ // UUID lod loaded (or not created) yet.
        android.content.SharedPreferences sharedPref = android.preference.PreferenceManager.getDefaultSharedPreferences(ctx);
        uuid = sharedPref.getString(SHARED_PREFS_UUID, "");

        if(uuid == null || uuid.isEmpty()){ // If UUID never been created, then create and store
            uuid = java.util.UUID.randomUUID().toString();

            SharedPreferences.Editor editor = sharedPref.edit();
            editor.putString(SHARED_PREFS_UUID,uuid);
            editor.commit();
        }
    }
    return uuid;
}

public static void openFile(android.content.Context context, java.io.File url) throws java.io.IOException {

    // Exemple of call :
    // File myFile = new File("your any type of file url");
    // FileOpen.openFile(mContext, myFile);

    // Create URI
    File file = url;
    android.net.Uri uri = Uri.fromFile(file);

    android.content.Intent intent = new Intent(Intent.ACTION_VIEW);
    // Check what kind of file you are trying to open, by comparing the url with extensions.
    // When the if condition is matched, plugin sets the correct intent (mime) type, 
    // so Android knew what application to use to open the file
    if (url.toString().contains(".doc") || url.toString().contains(".docx")) {
        // Word document
        intent.setDataAndType(uri, "application/msword");
    } else if (url.toString().contains(".pdf")) {
        // PDF file
        intent.setDataAndType(uri, "application/pdf");
    } else if (url.toString().contains(".ppt") || url.toString().contains(".pptx")) {
        // Powerpoint file
        intent.setDataAndType(uri, "application/vnd.ms-powerpoint");
    } else if (url.toString().contains(".xls") || url.toString().contains(".xlsx")) {
        // Excel file
        intent.setDataAndType(uri, "application/vnd.ms-excel");
    } else if (url.toString().contains(".zip") || url.toString().contains(".rar")) {
        // ZIP Files
        intent.setDataAndType(uri, "application/zip");
    } else if (url.toString().contains(".rtf")) {
        // RTF file
        intent.setDataAndType(uri, "application/rtf");
    } else if (url.toString().contains(".wav") || url.toString().contains(".mp3")) {
        // WAV audio file
        intent.setDataAndType(uri, "audio/x-wav");
    } else if (url.toString().contains(".gif")) {
        // GIF file
        intent.setDataAndType(uri, "image/gif");
    } else if (url.toString().contains(".jpg") || url.toString().contains(".jpeg") || url.toString().contains(".png")) {
        // JPG file
        intent.setDataAndType(uri, "image/jpeg");
    } else if (url.toString().contains(".txt")) {
        // Text file
        intent.setDataAndType(uri, "text/plain");
    } else if (url.toString().contains(".3gp") || url.toString().contains(".mpg") || url.toString().contains(".mpeg") || url.toString().contains(".mpe") || url.toString().contains(".mp4") || url.toString().contains(".avi")) {
        // Video files
        intent.setDataAndType(uri, "video/*");
    } else {
        //if you want you can also define the intent type for any other file

        //additionally use else clause below, to manage other unknown extensions
        //in this case, Android will show all applications installed on the device
        //so you can choose which application to use
        intent.setDataAndType(uri, "*/*");
    }

    intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
    context.startActivity(intent);
}

