private static final int REQUEST_$permission$ = 1000; // Any value can be used here

private void callAction(){
    if (androidx.core.content.ContextCompat.checkSelfPermission($context$,
            android.Manifest.permission.$permission$)
            != android.content.pm.PackageManager.PERMISSION_GRANTED) {

        // Permission is not granted
        // Should we show an explanation?
        if (androidx.core.app.ActivityCompat.shouldShowRequestPermissionRationale($context$,
                Manifest.permission.$permission$)) {
            // Show an explanation to the user *asynchronously* -- don't block
            // this thread waiting for the user's response! After the user
            // sees the explanation, try again to request the permission.
        } else {
            // No explanation needed; request the permission
            ActivityCompat.requestPermissions($context$,
                    new String[]{Manifest.permission.$permission$},
                    REQUEST_$permission$);

            // REQUEST_$permission$ is an
            // app-defined int constant. The callback method gets the
            // result of the request.
        }
    } else {
        // Permission has already been granted, do to the job/
        actionToBeCalled();
    }
}


