/**
 * Id to identify the request to the bunch of permissions.
 */
private static final int REQUEST_ALL_PERMISSIONS = 2000; // Any value can be used here


private void callAction(){
    // TODO : Fill the needed permissions to check
    String[] PERMISSIONS = {android.Manifest.permission.WRITE_EXTERNAL_STORAGE, android.Manifest.permission.READ_PHONE_STATE};

    if (!hasPermissions($context$, PERMISSIONS)) {

        // Permissions are not all granted
        // Should we show an explanation?
        if (shouldShowRequestPermissionRationale($context$,PERMISSIONS)) {
            // Show an explanation to the user *asynchronously* -- don't block
            // this thread waiting for the user's response! After the user
            // sees the explanation, try again to request the permission.
        } else {
            // No explanation needed; request the permission
            ActivityCompat.requestPermissions($context$,
                    PERMISSIONS,
                    REQUEST_ALL_PERMISSIONS);

            // REQUEST_ALL_PERMISSIONS is an
            // app-defined int constant. The callback method gets the
            // result of the request.
        }
    } else {
        // Permission has already been granted
    }
}


@Override
public void onRequestPermissionsResult(int requestCode,
String permissions[], int[] grantResults) {
    switch (requestCode) {
        case REQUEST_ALL_PERMISSIONS: {
            // If request is cancelled, the result arrays are empty.
            if (verifyPermissions(grantResults)) {
                // permission was granted, yay! Do the
                // contacts-related task you need to do.
            } else {
                // permission denied, boo! Disable the
                // functionality that depends on this permission.
            }
            return;
        }

        // other 'case' lines to check for other
        // permissions this app might request.

        default :
            super.onRequestPermissionsResult(requestCode, permissions, grantResults);
    }
}

/**
 * Check that all permissions have already been granted by verifying each entry in the given array.
 */
public static boolean hasPermissions(Context context, String... permissions) {
    if (context != null && permissions != null) {
        for (String permission : permissions) {
            if (ActivityCompat.checkSelfPermission(context, permission) != PackageManager.PERMISSION_GRANTED) {
                return false;
            }
        }
    }
    return true;
}

/**
 * Check if we should display more informations to the user by verifying each entry in the given array.
 */
public static boolean shouldShowRequestPermissionRationale(Context context, String... permissions) {
    if (context != null && permissions != null) {
        for (String permission : permissions) {
            if (ActivityCompat.shouldShowRequestPermissionRationale(context, permission)) {
                return true;
            }
        }
    }
    return false;
}

/**
 * Check that all given permissions have been granted by verifying that each entry in the
 * given array is of the value {@link PackageManager#PERMISSION_GRANTED}.
 *
 * @see Activity#onRequestPermissionsResult(int, String[], int[])
 */
public static boolean verifyPermissions(int[] grantResults) {
    // At least one result must be checked.
    if (grantResults.length < 1) {
        return false;
    }

    // Verify that each required permission has been granted, otherwise return false.
    for (int result : grantResults) {
        if (result != Pac kageManager.PERMISSION_GRANTED) {
            return false;
        }
    }
    return true;
}

