public static String getAppNameFromPackageName(Context context, String Packagename) {
    android.content.pm.PackageManager packageManager;
    android.content.pm.ApplicationInfo info;
    try {
        packageManager = context.getPackageManager();
        info = packageManager.getApplicationInfo(Packagename, PackageManager.GET_META_DATA);
        if(info != null){
            String appName = (String) packageManager.getApplicationLabel(info);
            return appName;
        } else {
            return "";
        }
    } catch (PackageManager.NameNotFoundException e) {
        e.printStackTrace();
        return "";
    } finally {
        // Cleanup
        packageManager = null;
        info = null;
    }
}

public static android.graphics.drawable.Drawable getAppIconFromPackageName(Context context, String Packagename) {
	android.content.pm.PackageManager packageManager;
	android.content.pm.ApplicationInfo info;
	try {
		packageManager = context.getPackageManager();
		info = packageManager.getApplicationInfo(Packagename, PackageManager.GET_META_DATA);
		if(info != null){
			Drawable icon = packageManager.getApplicationIcon(info);
			return icon;
		} else {
			return null;
		}
	} catch (PackageManager.NameNotFoundException e) {
		e.printStackTrace();
		return null;
	} finally {
		// Cleanup
		packageManager = null;
		info = null;
	}
}

