final android.speech.SpeechRecognizer recognizer = SpeechRecognizer.createSpeechRecognizer(MainActivity.this);
final android.content.Intent recognizerIntent = new Intent(android.speech.RecognizerIntent.ACTION_RECOGNIZE_SPEECH);
recognizerIntent.putExtra(RecognizerIntent.EXTRA_LANGUAGE_MODEL, RecognizerIntent.LANGUAGE_MODEL_FREE_FORM);
recognizerIntent.putExtra(RecognizerIntent.EXTRA_CALLING_PACKAGE, getPackageName());
recognizerIntent.putExtra(RecognizerIntent.EXTRA_PREFER_OFFLINE, true);

recognizer.setRecognitionListener(new android.speech.RecognitionListener() {
	@Override
