android.speech.tts.TextToSpeech myTTS;

myTTS = new TextToSpeech(getApplicationContext(), new TextToSpeech.OnInitListener() {
    @Override
    public void onInit(int status) {
        if (status != TextToSpeech.ERROR) {

            // #####################################
            // # Get information on the TTS engine #
            // #####################################
            // Get the engines available on the device
            java.util.List<TextToSpeech.EngineInfo> enginesAvailable = myTTS.getEngines();
            for (TextToSpeech.EngineInfo currentEngine : enginesAvailable) {
                if (BuildConfig.DEBUG) {
                    android.util.Log.d(TAG, "onInit engine available " + currentEngine.name + " " + currentEngine.label);
                }
            }

            if (android.os.Build.VERSION.SDK_INT > 21) {
                java.util.Set<java.util.Locale> availableLanguagesList = myTTS.getAvailableLanguages();
                for (Locale currentLocale : availableLanguagesList) {
                    if (BuildConfig.DEBUG) {
                        Log.d(TAG, "onInit locale available " + currentLocale.toString());
                    }
                }

                Set<android.speech.tts.Voice> availablesVoices = myTTS.getVoices();
                for (Voice currentVoice : availablesVoices) {
                    if (BuildConfig.DEBUG) {
                        Log.d(TAG, "onInit voice available locale " + currentVoice.getLocale());
                        Log.d(TAG, "onInit voice available name " + currentVoice.getName());
                        Log.d(TAG, "onInit voice available feature " + currentVoice.getFeatures());
                        Log.d(TAG, "onInit voice available latency " + currentVoice.getLatency());
                        Log.d(TAG, "onInit voice available quality " + currentVoice.getQuality());
                    }
                }
            }


            // Set the TTS language
            if (myTTS.isLanguageAvailable(Locale.ENGLISH) >= TextToSpeech.LANG_AVAILABLE) { // see TextToSpeech.LANG_AVAILABLE, ...
                myTTS.setLanguage(Locale.ENGLISH);
            }

            if (myTTS.isSpeaking()) {
                myTTS.stop();
            }

            // Optional
            // myTTS.setPitch(0.2f); // 1.0 is normal
            // myTTS.setSpeechRate(0.2f); // 1.0 is normal

            String messageToBeTTS = "Hello world";
            myTTS.speak(messageToBeTTS, TextToSpeech.QUEUE_FLUSH, null);
        }
    }
});

