// Send SMS
/*
 * BroadcastReceiver mBrSend; BroadcastReceiver mBrReceive;
 */
private void sendSMS(android.content.Context context, String phoneNumber, String message) {
	java.util.ArrayList<android.app.PendingIntent> sentPendingIntents = new ArrayList<PendingIntent>();
	ArrayList<PendingIntent> deliveredPendingIntents = new ArrayList<PendingIntent>();
	PendingIntent sentPI		= PendingIntent.getBroadcast(context, 0, new android.content.Intent(context, SmsSentReceiver.class), 0);
	PendingIntent deliveredPI	= PendingIntent.getBroadcast(context, 0, new Intent(context, SmsDeliveredReceiver.class), 0);
	try {
		android.telephony.SmsManager sms = SmsManager.getDefault();
		ArrayList<String> mSMSMessage = sms.divideMessage(message);
		for (int i = 0; i < mSMSMessage.size(); i++) {
			sentPendingIntents.add(i, sentPI);
			deliveredPendingIntents.add(i, deliveredPI);
		}
		sms.sendMultipartTextMessage(phoneNumber, null, mSMSMessage, sentPendingIntents, deliveredPendingIntents);

	} catch (Exception e) {
		e.printStackTrace();
		android.widget.Toast.makeText(getBaseContext(), "SMS sending failed...", Toast.LENGTH_SHORT).show();
	}
}

// TODO : add the permission to the Manifest file : <uses-permission android:name="android.permission.SEND_SMS" />

// TODO : create 2 classes :
// SmsDeliveredReceiver and replace the code by the code in the LiveTemplate : android_send_sms_02_SmsDeliveredReceiver
// SmsSentReceiver and replace the code by the code in the LiveTemplate : android_send_sms_03_SmsSentReceiver

// TODO : Add the receivers declaration to the Manifest file (inside the application tag :
// <receiver android:name=".SmsSentReceiver" />
// <receiver android:name=".SmsDeliveredReceiver" />

public class SmsDeliveredReceiver extends android.content.BroadcastReceiver {
    @Override
    public void onReceive(android.content.Context context, android.content.Intent arg1) {
        switch (getResultCode()) {
            case android.app.Activity.RESULT_OK:
                android.widget.Toast.makeText(context, "SMS delivered", Toast.LENGTH_SHORT).show();
                break;
            case Activity.RESULT_CANCELED:
                Toast.makeText(context, "SMS not delivered", Toast.LENGTH_SHORT).show();
                break;
        }
    }
}

public class SmsSentReceiver extends android.content.BroadcastReceiver {
    @Override
    public void onReceive(Context context, android.content.Intent arg1) {
        switch (getResultCode()) {
            case android.app.Activity.RESULT_OK:
                android.widget.Toast.makeText(context, "SMS Sent", Toast.LENGTH_SHORT).show();
                break;
            case android.telephony.SmsManager.RESULT_ERROR_GENERIC_FAILURE:
                Toast.makeText(context, "SMS generic failure", Toast.LENGTH_SHORT).show();
                break;
            case SmsManager.RESULT_ERROR_NO_SERVICE:
                Toast.makeText(context, "SMS no service", Toast.LENGTH_SHORT).show();
                break;
            case SmsManager.RESULT_ERROR_NULL_PDU:
                Toast.makeText(context, "SMS null PDU", Toast.LENGTH_SHORT).show();
                break;
            case SmsManager.RESULT_ERROR_RADIO_OFF:
                Toast.makeText(context, "SMS radio off", Toast.LENGTH_SHORT).show();
                break;
        }
    }
}

