 // START PROCESS
@Override
public void onStart() {
	super.onStart();
	if (BuildConfig.DEBUG) {
		Log.d(TAG, "onStart ");
	}
	android.content.IntentFilter filter = new IntentFilter();
	filter.addAction(Intent.ACTION_POWER_DISCONNECTED);
	filter.addAction(Intent.ACTION_POWER_CONNECTED);
	this.registerReceiver(mBroadcastReceiverPower, filter); // using activity context.
}

@Override
public void onStop() {
	if (BuildConfig.DEBUG) {
		Log.d(TAG, "onStop ");
	}
	this.unregisterReceiver(mBroadcastReceiverPower);
	super.onStop();
}

