private void enterPIP() {
    // $END$TODO: In the manifest, in the class, add:
    // android:supportsPictureInPicture="true"
    // More details : https://github.com/googlearchive/android-PictureInPicture
    if (android.os.Build.VERSION.SDK_INT >= 24) {
        if ($context$.getPackageManager().hasSystemFeature(android.content.pm.PackageManager.FEATURE_PICTURE_IN_PICTURE)) {
            $context$.enterPictureInPictureMode();
        }
    }
}

